__NUXT_JSONP__("/archives/jiunu", (function(a,b,c,d){return {data:[{archive:{id:"jiunu",createdAt:c,updatedAt:d,publishedAt:c,revisedAt:d,title:"jiunu Photographing",description:"Lifestyle Brand",direction:a,work:"Product Shoot",client:"jiunu by ADI",date:"2021-09-30T15:00:00.000Z",thumbnail:{url:"https:\u002F\u002Fimages.microcms-assets.io\u002Fassets\u002F381fe847d8974f9e9513ac2515c51dbe\u002F0ae3b61741404df9a13545b6095dd82c\u002Fti_05_jiunu_02.webp",height:3018,width:2156},body:"\u003Ch4 id=\"hbf4b51eae1\"\u003EOverview\u003C\u002Fh4\u003E\u003Ch1 id=\"h1df2342abb\"\u003E心と体の健やかな美しさ\u003C\u002Fh1\u003E\u003Cp\u003Ejiunuは中目黒のクリエイティブで斬新なダイニングメニューを提供するネパールダイニング ADIが展開するブランドで、心と体の健やかな美しさを提案するライフスタイルブランドです。生活の基礎である衣・食・住を整えることをより深く考え、様々なプロダクトを提案します。知性と感性を受け継ぎ、大切にし、国境を超え生活に豊かさをもたらすプロダクト・ライフスタイルの提案を行います。今回のプロジェクトでは、レモンアチャールやティムットペッパー、マシコウェア（オリジナルブランドのテーブルウェア）など、jiunuの代表的な商品を撮影しました。撮影ではコンセプトでもある健やかな美を表現することを目指すとともに、サンスクリット語で”生きる”を意味するブランドネーム”jiunu”を多面的に捉え、重みや威厳といったエッセンスを加えることで、よりコンセプトを体現したビジュアルを目指しました。\u003Cbr\u003E\u003Cbr\u003E\u003Cimg src=\"https:\u002F\u002Fimages.microcms-assets.io\u002Fassets\u002F381fe847d8974f9e9513ac2515c51dbe\u002F9a474c745bb14765a886326e87a6d5d5\u002Fti_05_jiunu_01.webp\" alt=\"\"\u003E\u003Cbr\u003Ejiunu by ADI - Lemon Achar\u003Cbr\u003E\u003Cbr\u003E\u003Cimg src=\"https:\u002F\u002Fimages.microcms-assets.io\u002Fassets\u002F381fe847d8974f9e9513ac2515c51dbe\u002F01f2d361bf9b423184f30f00bb7d77be\u002Fti_05_jiunu_03.webp\" alt=\"\"\u003E\u003Cbr\u003Ejiunu by ADI - Lemon Achar and Mashiko Wear\u003Cbr\u003E\u003Cbr\u003E\u003Cimg src=\"https:\u002F\u002Fimages.microcms-assets.io\u002Fassets\u002F381fe847d8974f9e9513ac2515c51dbe\u002F6da07e36c3b8470297555ae89b439f45\u002Fti_05_jiunu_04.webp\" alt=\"\"\u003E\u003Cbr\u003Ejiunu by ADI - Lemon Achar\u003Cbr\u003E\u003Cbr\u003E\u003Cimg src=\"https:\u002F\u002Fimages.microcms-assets.io\u002Fassets\u002F381fe847d8974f9e9513ac2515c51dbe\u002F36367d8a98134adfac5b77e6e65a4141\u002Fti_05_jiunu_05.webp\" alt=\"\"\u003E\u003Cbr\u003Ejiunu by ADI - Mashiko Wear\u003Cbr\u003E\u003Cbr\u003E\u003Cimg src=\"https:\u002F\u002Fimages.microcms-assets.io\u002Fassets\u002F381fe847d8974f9e9513ac2515c51dbe\u002Fff671f4707ba47bba663b2297eec3d8c\u002Fti_05_jiunu_07.webp\" alt=\"\"\u003E\u003Cbr\u003Ejiunu by ADI - Lemon Achar\u003Cbr\u003E\u003Cbr\u003E\u003Cimg src=\"https:\u002F\u002Fimages.microcms-assets.io\u002Fassets\u002F381fe847d8974f9e9513ac2515c51dbe\u002Fcb54389fd469480ca1a110abf3cf45d3\u002Fti_05_jiunu_06.webp\" alt=\"\"\u003E\u003Cbr\u003Ejiunu by ADI - Curry Pouch\u003C\u002Fp\u003E"},prev:{id:"lamie",title:"Lamie Photographing",date:"2021-11-30T15:00:00.000Z",direction:a,work:"Interior Shoot"},prev2:{id:"one",title:"one inc. Brand Site",date:"2022-01-31T15:00:00.000Z",direction:"Technical Direction",work:"UI Design, Motion Graphics, Web Development, Photography"},prev3:{id:"makina",title:"Makina Nakijin",date:"2022-03-11T15:00:00.000Z",direction:a,work:"Architect \u002F Interior Shoot"},next:{id:"thalassic",title:"Thalassic Capital",date:"2021-02-28T15:00:00.000Z",direction:"Visual Identity",work:"Logo, Graphic Design, UI Design, Motion Graphic, Web Development"},next2:b,next3:b}],fetch:{},mutations:b}}("Photography Direction",void 0,"2022-01-04T02:00:08.457Z","2022-03-23T06:25:02.041Z")));