__NUXT_JSONP__("/archives/thalassic", (function(a,b,c,d,e){return {data:[{archive:{id:"thalassic",createdAt:b,updatedAt:"2022-03-16T16:50:45.224Z",publishedAt:b,revisedAt:"2022-01-30T01:58:36.278Z",title:c,description:"A Capital Venture in Japan, Australia and China",direction:"Visual Identity",work:"Logo, Graphic Design, UI Design, Motion Graphic, Web Development",client:c,date:"2021-02-28T15:00:00.000Z",thumbnail:{url:"https:\u002F\u002Fimages.microcms-assets.io\u002Fassets\u002F381fe847d8974f9e9513ac2515c51dbe\u002Ffc31aa2b3a0a4a07ae4787c750f88f77\u002Fthalassic_wavy_cam.gif",height:600,width:900},body:"\u003Ch4 id=\"hbf4b51eae1\"\u003EOverview\u003C\u002Fh4\u003E\u003Ch1 id=\"h0b5b8f79c7\"\u003E豪・中・日を拠点に活躍する新進気鋭のキャピタルベンチャー\u003C\u002Fh1\u003E\u003Cp\u003Eオーストラリア・中国・日本を拠点に活動する新進気鋭のキャピタルベンチャーTHLASSIC CAPITALにそぐうダイナミックでエネルギッシュなビジュアルの構築を行いました。ワークスコープとしては、ロゴや販促物といったグラフィックデザインから、サイトのUIデザイン・開発、そしてメインビジュアルの撮影を含むアートディレクションを担当しています。\u003Cbr\u003E\u003Cbr\u003E\u003Cimg src=\"https:\u002F\u002Fimages.microcms-assets.io\u002Fassets\u002F381fe847d8974f9e9513ac2515c51dbe\u002Faac8639d4bca432286d954e1bcea9b71\u002Fthalassic_logo_guide.png\" alt=\"\"\u003E\u003C\u002Fp\u003E\u003Ch5 id=\"h636174e04e\"\u003EThalassic Capital - Logo Guide\u003C\u002Fh5\u003E\u003Cp\u003E\u003Cbr\u003E\u003Cimg src=\"https:\u002F\u002Fimages.microcms-assets.io\u002Fassets\u002F381fe847d8974f9e9513ac2515c51dbe\u002Fa732a749d2c2438c9e11b0578d71a566\u002Fthalassic_mark_guide.png\" alt=\"\"\u003E\u003C\u002Fp\u003E\u003Ch5 id=\"h39c799dbf3\"\u003EThalassic Capital - Logomark\u003C\u002Fh5\u003E\u003Cp\u003E\u003Cbr\u003E\u003Cimg src=\"https:\u002F\u002Fimages.microcms-assets.io\u002Fassets\u002F381fe847d8974f9e9513ac2515c51dbe\u002Fc9745cad3a764b49ad4da73213f2bcea\u002Fthalassic_logo_.png\" alt=\"\"\u003E\u003C\u002Fp\u003E\u003Ch5 id=\"h961ee9693c\"\u003EThalassic Capital - Logotype +α\u003C\u002Fh5\u003E\u003Cp\u003E\u003Cbr\u003E\u003Cimg src=\"https:\u002F\u002Fimages.microcms-assets.io\u002Fassets\u002F381fe847d8974f9e9513ac2515c51dbe\u002Fe31a16173f0a42c9acd1cb051bc31520\u002Flogo_guide.png\" alt=\"\"\u003E\u003C\u002Fp\u003E\u003Ch5 id=\"h823c1586e8\"\u003EThalassic Capital - Full Logo&nbsp;\u003C\u002Fh5\u003E\u003Cp\u003E\u003Cbr\u003E\u003C\u002Fp\u003E\u003Ch4 id=\"haacf67a32e\"\u003EDesign Theme\u003C\u002Fh4\u003E\u003Ch1 id=\"hc0cc036f38\"\u003E大海原に乗り出す若き船員たち\u003C\u002Fh1\u003E\u003Cp\u003E湾や海といった意味を持つ屋号のThalassicから、海のもつ大きなエネルギーを彼らのエネルギッシュさに見立て、ロゴ・カラーリング・タイプフェイスを通して表現しました。またこれから大海原に乗り出す船員たちをしっかりと支えていけるような、力強いボールドなデザインにし、キャピタルベンチャーとして重要な要素である、安心感を全体のビジュアルの構築を通して作り出しました。\u003Cbr\u003E\u003Cbr\u003E\u003Cimg src=\"https:\u002F\u002Fimages.microcms-assets.io\u002Fassets\u002F381fe847d8974f9e9513ac2515c51dbe\u002F291ca3531f1840c7b3cd66f44012001c\u002Fthalassic_web.webp\" alt=\"\"\u003E\u003C\u002Fp\u003E\u003Ch5 id=\"h3d8f960fad\"\u003EThalassic Capital - Web Design\u003C\u002Fh5\u003E"},prev:{id:d,title:d,date:"2021-09-30T15:00:00.000Z",direction:e,work:"Product Shoot"},prev2:{id:"lamie",title:"Lamie",date:"2021-11-30T15:00:00.000Z",direction:e,work:"Interior Shooting"},prev3:{id:"one",title:"one inc. Brand Site",date:"2022-01-31T15:00:00.000Z",direction:"Technical Direction",work:"UI Design, Motion Graphics, Web Development, Photography"},next:a,next2:a,next3:a}],fetch:{},mutations:a}}(void 0,"2022-01-28T00:03:26.099Z","Thalassic Capital","jiunu","Photography Direction")));